import React,{useState} from 'react';
import './style/index.css';
import Filter from './filter.js'

const Index = (props) => {

    const [spaces,filteredSpaces] = useState(props.spaces);
    const [falseLaunch,SucessfulLaunch] = useState(false);
    const [falseLanding,SucessfulLanding] = useState(false);
    const [launchYear,updatedYear] = useState(0);


    let showSucessfulLaunch = () =>{
        SucessfulLaunch(true);
        fetch('https://api.spacexdata.com/v3/launches?limit=100&amp;launch_success=true')
        .then(response => response.json())
        .then(data => filteredSpaces(data.filter(arr => arr.launch_success === true)));     
    } 

    let showSucessfulLanding = () => {
        SucessfulLanding(true);
        filteredSpaces(spaces.filter(arr => arr.rocket.first_stage.cores[0].land_success === true));
    }

    if (falseLaunch && falseLanding) {
        if (launchYear === 0) {
            fetch('https://api.spacexdata.com/v3/launches?limit=100&amp;launch_success=true&amp;land_success=true')
            .then(response => response.json())
            .then(data => filteredSpaces(data.filter(arr => arr.launch_success === true && arr.rocket.first_stage.cores[0].land_success === true)));
        } else {
            fetch('https://api.spacexdata.com/v3/launches?limit=100&amp;launch_success=true&amp;land_success=true&amp;launch_year=2014')
            .then(response => response.json())
            .then(data => filteredSpaces(data.filter(arr => arr.launch_success === true && arr.rocket.first_stage.cores[0].land_success === true && arr.launch_year == launchYear)));
        }
    }

    return (
        <React.Fragment>
        <div className="grid-container">
        <div className="grid-container1">
            <div className="grid-item1">
                <h>Filter</h>
                <p>Launch Year</p>
                <div className="row">
                    <div>
                        <button className="buttons" onClick={updatedYear(2006)}>2006</button>
                        <button className="buttons" onClick={updatedYear(2006)}>2008</button>
                        <button className="buttons" onClick={updatedYear(2006)}>2010</button>
                        <button className="buttons" onClick={updatedYear(2006)}>2012</button>
                        <button className="buttons" onClick={updatedYear(2006)}>2014</button>
                        <button className="buttons" onClick={updatedYear(2006)}>2016</button>
                        <button className="buttons" onClick={updatedYear(2006)}>2018</button>
                        <button className="buttons" onClick={updatedYear(2006)}>2020</button>
                    </div>
                    <div>
                    <   button className="buttons" onClick={updatedYear(2006)}>2007</button>
                        <button className="buttons" onClick={updatedYear(2006)}>2009</button>
                        <button className="buttons" onClick={updatedYear(2006)}>2011</button>
                        <button className="buttons" onClick={updatedYear(2006)}>2013</button>
                        <button className="buttons" onClick={updatedYear(2006)}>2015</button>
                        <button className="buttons" onClick={updatedYear(2006)}>2017</button>
                        <button className="buttons" onClick={updatedYear(2006)}>2019</button>
                    </div>
                </div>
                <p>Sucessful launch</p>
                <div className="row">
                    <div><button className="buttons" onClick={showSucessfulLaunch}>true</button></div>
                    <div><button className="buttons">false</button></div>
                </div>
                <p>Sucessful landing</p>
                <div className="row">
                <div><button className="buttons" onClick={showSucessfulLanding}>true</button></div>
                <div><button className="buttons">false</button></div>
                </div>
            </div>
        </div>


            <div className="grid-container">
                {
                    spaces.map( spaceData => {
                        return  (

                            <div className="grid-item">
                                <img src={spaceData.links.mission_patch_small} alt="Mountains" />
                                <h4>{spaceData.mission_name}</h4>
                                <p>Mission Ids: {spaceData.mission_id}</p>
                                <p>Launch Year: {spaceData.launch_year}</p>
                                <p>sucessfull Launch: {spaceData.launch_success? 'true' : 'false'}</p>
                                <p>sucessfull Landing: {spaceData.rocket.first_stage.cores[0].land_success ? 'true' : false}</p>
                            </div>

                        )
                    })
                }
            </div>
            </div>
        </React.Fragment>

    );
}
export const getStaticProps = async () => {
    const res = await fetch('https://api.spacexdata.com/v3/launches?limit=100')
    const spaces = await res.json()
    return {
        props: {
            spaces
        },
    }
  }

export default Index;
